mapper.select:
SELECT d.* FROM ( SELECT individual_sn, MAX(reset_times) AS max_times FROM tb_individual_flow GROUP BY individual_sn ) t INNER JOIN tb_individual_flow d ON d.reset_times = t.max_times AND d.individual_sn = t.individual_sn AND d.individual_sn IN
#{item}
AND d.oper = #{oper}
AND d. STATUS = #{status}

SELECT t1.* FROM tb_individual_flow t1 RIGHT JOIN ( SELECT individual_sn, MAX(oper_time) AS operTime FROM tb_individual_flow where individual_sn IN
#{item}
GROUP BY individual_sn ) t2 ON t1.individual_sn = t2.individual_sn AND t1.oper_time = t2.operTime
